var menuOpen=false;
/* + * x * + * x * + * x * + * x * + * x * + * x

Take divs in the #widgets and turn it into an
array to be later populated with widgets

x * + * x * + * x * + * x * + * x * + * x * + */
    
function makeWidgetsArray(){
	var widget_list = [];
	$('#widgets').children('div').each(function(){
		widget_list.push(this.className);
	});
	return widget_list;

}

/* + * x * + * x * + * x * + * x * + * x * + * x

Pull a random value from array

x * + * x * + * x * + * x * + * x * + * x * + */
    
function randomFromArray(theArray){
		random_index=Math.floor((Math.random() * theArray.length));
		return "<strong>" + capitalizeFirstLetter(theArray[random_index]) + "</strong>";
	}


/* + * x * + * x * + * x * + * x * + * x * + * x

Capitalize the first letter of a string

x * + * x * + * x * + * x * + * x * + * x * + */
    

function capitalizeFirstLetter(string) {return string.charAt(0).toUpperCase() + string.slice(1);}



/* + * x * + * x * + * x * + * x * + * x * + * x

Open the Menu

x * + * x * + * x * + * x * + * x * + * x * + */
    
function openMenu(){
	if(menuOpen===false){
		$('#menu').animate({'top':'6em'},400,'easeOutQuart');
		menuOpen=true;
	}
	else{
		$('#menu').animate({'top':'-4em'},400,'easeOutBounce');
		menuOpen=false;
	}
	
}


/* + * x * + * x * + * x * + * x * + * x * + * x

Return a Random Prompt

x * + * x * + * x * + * x * + * x * + * x * + */


function returnPrompt(options_list, option){
	var random_index=0;
	var merged_words=[];


	//If choice is random, start by picking a random choice.
	if(option==="Random"){
		random_index=Math.floor((Math.random() * (options_list.length-1)) + 1);
		option= options_list[random_index]["Short"];
	}


	// Pull random from objects
	// For a single Noun
	if(option==="Noun"){
		merged_words = $.merge(words["nouns"],words["professions"]);
		return randomFromArray(merged_words);
	}
	// For a Noun + Adjective
	if(option==="AdjectiveNoun"){
		merged_words = $.merge(words["nouns"],words["professions"]);
		return randomFromArray(words["adjectives"]) + " " + randomFromArray(merged_words);
	}

	// For Profession
	if(option==="Profession"){
		return randomFromArray(words["professions"]);
	}

	// For Phrase
	if(option==="Phrase"){
		random_index=Math.floor((Math.random() * 16));//number of options
		switch (random_index) {
		    case 0:
		    	merged_words = $.merge(words["interests"],words["professions"]);
		        return "World's Worst " + randomFromArray(merged_words);
		        break;
		    case 1:
		        return "Ways " + randomFromArray(words["interests"]) + " is overrated";
		        break;
		    case 2:	
		        return "Ways to implement advertising into " + randomFromArray(words["interests"]);
		        break;
		    case 3:
		        return "How to win at " + randomFromArray(words["interests"]);
		        break;
		    case 4:
		    	merged_words = $.merge(words["nouns"],words["professions"]);
		        return "A(n) " + randomFromArray(merged_words) + " versus a(n) " + randomFromArray(merged_words) ;
		        break;
		    case 5:
		    	merged_words = $.merge(words["nouns"],words["professions"],words["interests"]);
		    	return randomFromArray(merged_words) + ": The Movie Monster";
		    	break;
		    case 6:
		    	merged_words = $.merge(words["nouns"],words["professions"],words["interests"]);
		    	return randomFromArray(merged_words) + ": The Government Conspiracy";
		        break;
		    case 7:
		        return "The most embarrassing thing that can happen as a " + randomFromArray(words["professions"]);
		        break;
		    case 8:
		        return "How " + randomFromArray(words["interests"]) + " is impacting the life of a " + randomFromArray(words["professions"]);
		        break;
		    case 9:
		    	merged_words = $.merge(words["nouns"],words["professions"]);
		        return "A " + randomFromArray(merged_words) + " riding a " + randomFromArray(merged_words);
		        break;
		    case 10:
		        return "How to troll a " + randomFromArray(words["professions"]);
		        break;
		    case 11:
		    	merged_words = $.merge(words["nouns"],words["professions"],words["interests"]);
		    	return "I like my women like I like my " + randomFromArray(merged_words);
		        break;
		    case 12:
		    	merged_words = $.merge(words["nouns"],words["professions"],words["interests"],words["adjectives"]);
		    	return "The Adventures of " + randomFromArray(merged_words) + " Man";
		        break;
		    case 13:
		    	merged_words = $.merge(words["professions"],words["interests"]);
		    	return "The type of person that would go to " + randomFromArray(merged_words) + "-Con (Conference)";
		        break;
		    case 14:
		    	merged_words = $.merge(words["professions"],words["interests"]);
		    	return "A(n) " + randomFromArray(merged_words) + " themed theme park";
		        break;
		    case 15:
		    	merged_words = $.merge(words["nouns"],words["professions"],words["interests"]);
		    	return "The Adventures of the " + randomFromArray(words["adjectives"]) + " " + randomFromArray(merged_words);
		        break;

		}
	}

}
